import {pieces} from '../PieceData';


export const checkPiece = (x, y) => 
{
    console.log("x c : " + x);
    console.log("y c : " + y);
    for(let p of pieces) {
        console.log('p.x: ' + p.x);
        console.log('p.y: ' + p.y);
        if(x !== p.x && y !== p.y)
        {
            console.log("K");
            return true;  

        }
    };
    return false;
}
export const checkIfSame = (pX, pY, dX, dY) => {
    if(pX === dX && pY === dY)
    {
        console.log("TRUE TOO");
