export const horse = (pX, dX, pY, dY) => {
    console.log("position Y: " + (dY - pY));
    console.log("position X: " + (dX - pX));

    if(((dX - pX) === 1 && ((dY - pY) === 2)))
    {
        console.log("Move is valid");
        return true;
    }
    else if(((dX - pX) === -1 && ((dY - pY) === 2)))
    {
        console.log("Move is valid");
        return true;
    }
    else if(((dX - pX) === 1 && ((dY - pY) === -2)))
    {
        console.log("Move is valid");
        return true;
    }
    else if(((dX - pX) === -1 && ((dY - pY) === -2)))
    {
        console.log("Move is valid");
        return true;
    }
    else
    {
        return false;
    }
}
