export const tower = (pX, dX, pY, dY) => {
    if(((dX - pX) <= 8 || (dX - pX >= 0)) && (dY === pY))
    {
        console.log("Move is Valid");
        return true;
    }
    else if(((dY - pY) <= 8 || (dY - pY >= 1)) && (dX === pX))
    {
        console.log("Move is Valid");
        return true;
    }
    else
    {
        return false;
    }
}
