import {pieces} from '../PieceData';
export const king = (positionY, desiredPositionY, positionX, desiredPositionX) => {
    //Basic movements
    if((desiredPositionY - positionY) === 1 || (desiredPositionY - positionY) === -1)
    {
        console.log("Move is valid");
        return true;
    }
    else if((desiredPositionX - positionX) === 1 || (desiredPositionX - positionX) === -1)
    {
        console.log("Move is Valid");
        return true;
    }
    else
    {
        return false;
    }
}
