import { pieces } from "../PieceData";

export const queen = (pX, dX, pY, dY) => {
  let cont = true;

  let attack = false;
  pieces.forEach((p) => {
    console.log("piece is fine");
    if (p.x === dX && p.y === dY) {
      cont = false;
      console.log("cont " + cont);

    }
    else if((p.x === dX && p.y === dY) && attack)
    {
      attack = true;
    }

  });
  if( attack && (dX - pX <= 8 || dX - pX >= 0 || dY - pY <= 8 || dY - pY >= 0))
  {
    console.log("Attack !");
    return true;
  }
  //Basic Movements
  if (cont) {
    if (dX - pX <= 8 || dX - pX >= 0 || dY - pY <= 8 || dY - pY >= 0) {
      console.log("Move is valid");
      return true;
    } else {
      console.log("Move isn't valid");
      return false;
    }
  } 
};
