import { pieces } from "../PieceData";
import {checkPiece} from "./General";
export const bishop = (pX, dX, pY, dY) => {
  for(var i = 1; i < 8; i++)
  {
    if(dX < pX && dY < pY)
    {
      if(checkPiece((pX - i), (pY - i)))
      {
        return true;
      }

    }
    else if(dX > pX && dY > pY)
    {

      if(checkPiece((pX + i),  (pY + i)))
      {
        return true;
      }
    }
    else if(dX > pX && dY < pY)
    {
      if(checkPiece((pX + i), (pY - i)))
      {
        return true;
      }


    }
    else if(dX < pX && dY > pY)
    {
      if(checkPiece((pX - i), (pY + i)))
      {
        return true;
      }
    }
  }
};
