import {pieces} from '../PieceData';


export const checkPiece = (x, y) => 
{
    var truth  = false;
    console.log('x: ' + x + ' y: ' + y);
    pieces.forEach(p =>  {
        console.log('p.x: ' + p.x + ' p.y: ' + p.y);

        if(x !== p.x && y !== p.y)
        {
            truth = true;
        }
        else
        {
            truth = false;
            return truth;
        }
    });    
    return truth;
}
export const checkIfSame = (pX, pY, dX, dY) => {
    if(pX === dX && pY === dY)
    {
        console.log("TRUE TOO");
        return true;
    }
}
