import { pieces } from "../PieceData";
import { checkIfSame, checkPiece } from "./General";

export const queen = (pX, dX, pY, dY) => {
  for(var i = 0; i < 8; i++)
  {
    if(checkPiece((pX + i), (pY + i)))
    {
      if(checkIfSame(pX + i, pY + i, dX, dY))
      {
        return true;
      }
    }
    else
    {
      break;
    }
  }
};
