export const bishop = (pX, dX, pY, dY) => {
    if(((dX - pX) <= 8 || (dX-pX) >= 0) && ((dX != pX) || (dY != pY)))
    {
        console.log("Move is valid");
        return true;
    }
    else
    {
        console.log("Move isn't valid");
        return false;
    }

}
