export const horse = (pX, dX, pY, dY) => {
    console.log(dY - pY);
    console.log(dX - pX);
    if(((dX - pX) === 1 || (dX - pX) === - 1) && ((dY - pY) === 2) || (dY - pY === -2))
    {
        console.log("Move is valid");
        return true;
    }
    else
    {
        return false;
    }
}
