export const horse = (pX, dX, pY, dY) => {
    if(((dX - pX) === 1 || (dX - pX) === - 1) && ((dY - pY) === 3) || (dY - pY === -3))
    {
        console.log("Move is valid");
        return true;
    }
    else
    {
        return false;
    }
}
