import { pieces } from "../PieceData";
export const bishop = (pX, dX, pY, dY) => {
  let cont = true;
  let attack = false;
  pieces.forEach((p) => {

    if (p.x === dX && p.y === dY) {
      cont = false;

    }
    if(p.x === dX && p.y === dY && p.color === "black")
    {
      attack = true;
      p.x = null;
      p.y = null;
      console.log("Attack: " + attack);
    }
  });
  if(attack)
  {
    return true;
  }
  if (cont) {
    if(dX < pX && dY < pY)
    {
      return true;
    }
    else if(dX > pX && dY > dY)
    {
      return true;
    }
    else if(dX < pX && dY > dY)
    {
      return true;
    }
    else if(dX > pX && dY < dY)
    {
      return true;
    }

  } else {
    return false;
  }
};
