import { pieces } from "../PieceData";
import { checkIfSame, checkPiece } from "./General";

export const tower = (pX, dX, pY, dY) => {
  var mult = 0;
  dX < pX ? mult = -1 : mult = 1;
  for(var i = 0; i < 8; i++)
  {
    if(dX - pX < 8 || dX - pX < 0)
    {
      if(checkPiece((pX + i), pY))
      {
        if(checkIfSame((pX + i), pY, dX, dY))
        {
          return true;
        }
        break;
      }
    }
    if(dY - pY < 8 || dY - pY < 0)
    {
      if(checkPiece((pX), (pY + i)))
      {
        if(checkIfSame((pX), (pY+ i), dX, dY))
        {
          return true;
        }
        break;
      }
    }


  }
};
