import React from 'react';
import {pawn} from './Pawn';
import {king} from './King';
import {tower} from './Tower';
export const rule = (piece, pY, dY, pX, dX) => 
{

    switch(piece)
    {
        case "pawn":
           return pawn(pY, dY); 
           break;
        case "king":
            return king(pY, dY, pX, dX);
            break;

    }
}
