export const queen = (pX, dX, pY, dY) => {
    //Basic Movements
    if((dX - pX) <= 8 || (dX-pX) >= 0 ||(dY - pY) <= 8 || (dY-pY) >= 0 )
    {
        console.log("Move is valid");
        return true;
    }
    else
    {
        console.log("Move isn't valid");
        return false;
    }

}
